import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { authenticateToken, requireRole, generateToken, hashPassword, comparePassword, type AuthenticatedRequest } from "./middleware/auth";
import { 
  insertUserSchema, insertStoreSchema, insertRatingSchema, 
  loginSchema, signupSchema, passwordSchema, updatePasswordSchema 
} from "@shared/schema";
import { Request, Response } from "express";

export async function registerRoutes(app: Express): Promise<Server> {

  // Auth routes
  app.post('/api/auth/signup', async (req: Request, res: Response) => {
    try {
      console.log('Signup request body:', req.body);
      // Validate the entire signup request
      const validatedData = signupSchema.parse(req.body);
      console.log('Validated data:', validatedData);

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(validatedData.email);
      if (existingUser) {
        return res.status(400).json({ message: 'User with this email already exists' });
      }

      // Hash password
      const passwordHash = await hashPassword(validatedData.password);

      // Create user (exclude password and storeName from validatedData)
      const { password: _, storeName, ...userData } = validatedData;
      const user = await storage.createUser({
        ...userData,
        passwordHash,
      });

      // Create store for store owners
      if (user.role === 'store_owner') {
        const storeData = {
          name: req.body.storeName || `${user.name}'s Store`,
          address: user.address,
          ownerId: user.id,
        };
        await storage.createStore(storeData);
      }

      // Generate token
      const token = generateToken({
        id: user.id,
        email: user.email,
        role: user.role,
      });

      res.status(201).json({
        token,
        user: {
          id: user.id,
          name: user.name,
          email: user.email,
          role: user.role,
        },
      });
    } catch (error: any) {
      console.error('Signup error:', error);
      if (error.name === 'ZodError') {
        return res.status(400).json({
          message: 'Validation error',
          errors: error.errors,
        });
      }
      res.status(500).json({ message: 'Internal server error', error: error.message });
    }
  });

  app.post('/api/auth/login', async (req: Request, res: Response) => {
    try {
      const { email, password } = loginSchema.parse(req.body);

      // Find user
      const user = await storage.getUserByEmail(email);
      if (!user) {
        return res.status(401).json({ message: 'Invalid credentials' });
      }

      // Verify password
      const isValidPassword = await comparePassword(password, user.passwordHash);
      if (!isValidPassword) {
        return res.status(401).json({ message: 'Invalid credentials' });
      }

      // Generate token
      const token = generateToken({
        id: user.id,
        email: user.email,
        role: user.role,
      });

      res.json({
        token,
        user: {
          id: user.id,
          name: user.name,
          email: user.email,
          role: user.role,
        },
      });
    } catch (error: any) {
      if (error.name === 'ZodError') {
        return res.status(400).json({
          message: 'Validation error',
          errors: error.errors,
        });
      }
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  app.put('/api/auth/update-password', authenticateToken, async (req: AuthenticatedRequest, res: Response) => {
    try {
      const { currentPassword, newPassword } = updatePasswordSchema.parse(req.body);

      const user = await storage.getUser(req.user!.id);
      if (!user) {
        return res.status(404).json({ message: 'User not found' });
      }

      // Verify current password
      const isValidPassword = await comparePassword(currentPassword, user.passwordHash);
      if (!isValidPassword) {
        return res.status(400).json({ message: 'Current password is incorrect' });
      }

      // Hash new password
      const newPasswordHash = await hashPassword(newPassword);

      // Update password
      const success = await storage.updateUserPassword(user.id, newPasswordHash);
      if (!success) {
        return res.status(500).json({ message: 'Failed to update password' });
      }

      res.json({ message: 'Password updated successfully' });
    } catch (error: any) {
      if (error.name === 'ZodError') {
        return res.status(400).json({
          message: 'Validation error',
          errors: error.errors,
        });
      }
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  // User profile routes
  app.get('/api/user/profile', authenticateToken, async (req: AuthenticatedRequest, res: Response) => {
    try {
      const user = await storage.getUser(req.user!.id);
      if (!user) {
        return res.status(404).json({ message: 'User not found' });
      }

      res.json({
        id: user.id,
        name: user.name,
        email: user.email,
        role: user.role,
        address: user.address,
      });
    } catch (error) {
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  app.put('/api/user/profile', authenticateToken, async (req: AuthenticatedRequest, res: Response) => {
    try {
      const updates = {
        name: req.body.name,
        address: req.body.address,
      };

      const user = await storage.updateUser(req.user!.id, updates);
      if (!user) {
        return res.status(404).json({ message: 'User not found' });
      }

      res.json({
        id: user.id,
        name: user.name,
        email: user.email,
        role: user.role,
        address: user.address,
      });
    } catch (error) {
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  // Admin routes
  app.get('/api/admin/stats', authenticateToken, requireRole(['admin']), async (req: AuthenticatedRequest, res: Response) => {
    try {
      const stats = await storage.getStats();
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  app.get('/api/admin/users', authenticateToken, requireRole(['admin']), async (req: AuthenticatedRequest, res: Response) => {
    try {
      const search = req.query.search as string;
      const role = req.query.role as string;
      const users = await storage.getUsersWithFilters(search, role);
      res.json(users);
    } catch (error) {
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  app.post('/api/admin/users', authenticateToken, requireRole(['admin']), async (req: Request, res: Response) => {
    try {
      const userData = {
        name: req.body.name,
        email: req.body.email,
        address: req.body.address,
        role: req.body.role || 'user'
      };
      const validatedData = insertUserSchema.omit({ passwordHash: true }).parse(userData);
      const password = req.body.password;
      passwordSchema.parse(password);

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(validatedData.email);
      if (existingUser) {
        return res.status(400).json({ message: 'User with this email already exists' });
      }

      // Hash password
      const passwordHash = await hashPassword(password);

      // Create user
      const user = await storage.createUser({
        ...validatedData,
        passwordHash,
      });

      res.status(201).json({
        id: user.id,
        name: user.name,
        email: user.email,
        role: user.role,
        address: user.address,
      });
    } catch (error: any) {
      if (error.name === 'ZodError') {
        return res.status(400).json({
          message: 'Validation error',
          errors: error.errors,
        });
      }
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  app.delete('/api/admin/users/:id', authenticateToken, requireRole(['admin']), async (req: AuthenticatedRequest, res: Response) => {
    try {
      const success = await storage.deleteUser(req.params.id);
      if (!success) {
        return res.status(404).json({ message: 'User not found' });
      }
      res.json({ message: 'User deleted successfully' });
    } catch (error) {
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  app.get('/api/admin/stores', authenticateToken, requireRole(['admin']), async (req: AuthenticatedRequest, res: Response) => {
    try {
      const stores = await storage.getAllStores();
      res.json(stores);
    } catch (error) {
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  app.post('/api/admin/stores', authenticateToken, requireRole(['admin']), async (req: Request, res: Response) => {
    try {
      const storeData = insertStoreSchema.parse(req.body);

      // Verify owner exists and is a store owner
      const owner = await storage.getUser(storeData.ownerId);
      if (!owner) {
        return res.status(400).json({ message: 'Store owner not found' });
      }
      if (owner.role !== 'store_owner') {
        return res.status(400).json({ message: 'User must be a store owner' });
      }

      // Check if owner already has a store
      const existingStore = await storage.getStoreByOwnerId(storeData.ownerId);
      if (existingStore) {
        return res.status(400).json({ message: 'Store owner already has a store' });
      }

      const store = await storage.createStore(storeData);
      res.status(201).json(store);
    } catch (error: any) {
      if (error.name === 'ZodError') {
        return res.status(400).json({
          message: 'Validation error',
          errors: error.errors,
        });
      }
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  app.delete('/api/admin/stores/:id', authenticateToken, requireRole(['admin']), async (req: AuthenticatedRequest, res: Response) => {
    try {
      const success = await storage.deleteStore(req.params.id);
      if (!success) {
        return res.status(404).json({ message: 'Store not found' });
      }
      res.json({ message: 'Store deleted successfully' });
    } catch (error) {
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  // Store routes
  app.get('/api/stores', authenticateToken, async (req: AuthenticatedRequest, res: Response) => {
    try {
      const search = req.query.search as string;
      const address = req.query.address as string;
      const stores = await storage.searchStores(search, address);

      // Get user's ratings for these stores
      const userRatings = await storage.getUserRatings(req.user!.id);
      const userRatingMap = new Map(userRatings.map(rating => [rating.storeId, rating]));

      const storesWithUserRatings = stores.map(store => ({
        ...store,
        userRating: userRatingMap.get(store.id) || null,
      }));

      res.json(storesWithUserRatings);
    } catch (error) {
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  app.get('/api/stores/my-store', authenticateToken, requireRole(['store_owner']), async (req: AuthenticatedRequest, res: Response) => {
    try {
      const store = await storage.getStoreByOwnerId(req.user!.id);
      if (!store) {
        return res.status(404).json({ message: 'Store not found' });
      }

      const storeWithRatings = await storage.getStoreWithRatings(store.id);
      res.json(storeWithRatings);
    } catch (error) {
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  // Rating routes
  app.post('/api/ratings', authenticateToken, requireRole(['user']), async (req: AuthenticatedRequest, res: Response) => {
    try {
      const ratingData = insertRatingSchema.parse({
        ...req.body,
        userId: req.user!.id,
      });

      // Check if rating already exists
      const existingRating = await storage.getRating(ratingData.userId, ratingData.storeId);
      if (existingRating) {
        return res.status(400).json({ message: 'You have already rated this store. Use PUT to update your rating.' });
      }

      // Verify store exists
      const store = await storage.getStore(ratingData.storeId);
      if (!store) {
        return res.status(404).json({ message: 'Store not found' });
      }

      const rating = await storage.createRating(ratingData);
      res.status(201).json(rating);
    } catch (error: any) {
      if (error.name === 'ZodError') {
        return res.status(400).json({
          message: 'Validation error',
          errors: error.errors,
        });
      }
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  app.put('/api/ratings/:storeId', authenticateToken, requireRole(['user']), async (req: AuthenticatedRequest, res: Response) => {
    try {
      const ratingValue = insertRatingSchema.shape.ratingValue.parse(req.body.ratingValue);

      const rating = await storage.updateRating(req.user!.id, req.params.storeId, ratingValue);
      if (!rating) {
        return res.status(404).json({ message: 'Rating not found' });
      }

      res.json(rating);
    } catch (error: any) {
      if (error.name === 'ZodError') {
        return res.status(400).json({
          message: 'Validation error',
          errors: error.errors,
        });
      }
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  app.delete('/api/ratings/:storeId', authenticateToken, requireRole(['user']), async (req: AuthenticatedRequest, res: Response) => {
    try {
      const success = await storage.deleteRating(req.user!.id, req.params.storeId);
      if (!success) {
        return res.status(404).json({ message: 'Rating not found' });
      }
      res.json({ message: 'Rating deleted successfully' });
    } catch (error) {
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, stores, ratings, 
  type User, type InsertUser, type UserWithStore,
  type Store, type InsertStore, type StoreWithOwner,
  type Rating, type InsertRating, type RatingWithUser, type StoreWithRatings
} from "@shared/schema";
import { db } from "./db";
import { eq, and, avg, count, desc, or, ilike } from "drizzle-orm";

export interface IStorage {
  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<InsertUser>): Promise<User | undefined>;
  deleteUser(id: string): Promise<boolean>;
  getAllUsers(): Promise<UserWithStore[]>;
  getUsersWithFilters(search?: string, role?: string): Promise<UserWithStore[]>;
  updateUserPassword(id: string, passwordHash: string): Promise<boolean>;

  // Store methods
  getStore(id: string): Promise<Store | undefined>;
  getStoreByOwnerId(ownerId: string): Promise<Store | undefined>;
  createStore(store: InsertStore): Promise<Store>;
  updateStore(id: string, updates: Partial<InsertStore>): Promise<Store | undefined>;
  deleteStore(id: string): Promise<boolean>;
  getAllStores(): Promise<StoreWithOwner[]>;
  searchStores(search?: string, address?: string): Promise<StoreWithOwner[]>;
  getStoreWithRatings(id: string): Promise<StoreWithRatings | undefined>;

  // Rating methods
  getRating(userId: string, storeId: string): Promise<Rating | undefined>;
  createRating(rating: InsertRating): Promise<Rating>;
  updateRating(userId: string, storeId: string, ratingValue: number): Promise<Rating | undefined>;
  deleteRating(userId: string, storeId: string): Promise<boolean>;
  getRatingsByStore(storeId: string): Promise<RatingWithUser[]>;
  getUserRatings(userId: string): Promise<Rating[]>;

  // Stats methods
  getStats(): Promise<{ totalUsers: number; totalStores: number; totalRatings: number; }>;
}

export class DatabaseStorage implements IStorage {
  // User methods
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async updateUser(id: string, updates: Partial<InsertUser>): Promise<User | undefined> {
    const [user] = await db
      .update(users)
      .set(updates)
      .where(eq(users.id, id))
      .returning();
    return user || undefined;
  }

  async deleteUser(id: string): Promise<boolean> {
    const result = await db.delete(users).where(eq(users.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  async getAllUsers(): Promise<UserWithStore[]> {
    const result = await db
      .select()
      .from(users)
      .leftJoin(stores, eq(users.id, stores.ownerId))
      .orderBy(users.createdAt);

    return result.map(row => ({
      ...row.users,
      ownedStore: row.stores || undefined,
    }));
  }

  async getUsersWithFilters(search?: string, role?: string): Promise<UserWithStore[]> {
    let baseQuery = db
      .select()
      .from(users)
      .leftJoin(stores, eq(users.id, stores.ownerId));

    const conditions = [];
    if (search) {
      conditions.push(
        or(
          ilike(users.name, `%${search}%`),
          ilike(users.email, `%${search}%`),
          ilike(users.address, `%${search}%`)
        )
      );
    }
    if (role) {
      conditions.push(eq(users.role, role));
    }

    const result = conditions.length > 0 
      ? await baseQuery.where(and(...conditions)).orderBy(users.createdAt)
      : await baseQuery.orderBy(users.createdAt);

    return result.map(row => ({
      ...row.users,
      ownedStore: row.stores || undefined,
    }));
  }

  async updateUserPassword(id: string, passwordHash: string): Promise<boolean> {
    const result = await db
      .update(users)
      .set({ passwordHash })
      .where(eq(users.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  // Store methods
  async getStore(id: string): Promise<Store | undefined> {
    const [store] = await db.select().from(stores).where(eq(stores.id, id));
    return store || undefined;
  }

  async getStoreByOwnerId(ownerId: string): Promise<Store | undefined> {
    const [store] = await db.select().from(stores).where(eq(stores.ownerId, ownerId));
    return store || undefined;
  }

  async createStore(insertStore: InsertStore): Promise<Store> {
    const [store] = await db
      .insert(stores)
      .values(insertStore)
      .returning();
    return store;
  }

  async updateStore(id: string, updates: Partial<InsertStore>): Promise<Store | undefined> {
    const [store] = await db
      .update(stores)
      .set(updates)
      .where(eq(stores.id, id))
      .returning();
    return store || undefined;
  }

  async deleteStore(id: string): Promise<boolean> {
    const result = await db.delete(stores).where(eq(stores.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  async getAllStores(): Promise<StoreWithOwner[]> {
    const result = await db
      .select({
        store: stores,
        owner: users,
        averageRating: avg(ratings.ratingValue),
        totalRatings: count(ratings.id),
      })
      .from(stores)
      .innerJoin(users, eq(stores.ownerId, users.id))
      .leftJoin(ratings, eq(stores.id, ratings.storeId))
      .groupBy(stores.id, users.id)
      .orderBy(stores.createdAt);

    return result.map(row => ({
      ...row.store,
      owner: row.owner,
      averageRating: row.averageRating ? parseFloat(row.averageRating) : 0,
      totalRatings: row.totalRatings || 0,
    }));
  }

  async searchStores(search?: string, address?: string): Promise<StoreWithOwner[]> {
    let baseQuery = db
      .select({
        store: stores,
        owner: users,
        averageRating: avg(ratings.ratingValue),
        totalRatings: count(ratings.id),
      })
      .from(stores)
      .innerJoin(users, eq(stores.ownerId, users.id))
      .leftJoin(ratings, eq(stores.id, ratings.storeId));

    const conditions = [];
    if (search) {
      conditions.push(ilike(stores.name, `%${search}%`));
    }
    if (address) {
      conditions.push(ilike(stores.address, `%${address}%`));
    }

    const result = conditions.length > 0 
      ? await baseQuery.where(and(...conditions)).groupBy(stores.id, users.id).orderBy(stores.createdAt)
      : await baseQuery.groupBy(stores.id, users.id).orderBy(stores.createdAt);

    return result.map(row => ({
      ...row.store,
      owner: row.owner,
      averageRating: row.averageRating ? parseFloat(row.averageRating) : 0,
      totalRatings: row.totalRatings || 0,
    }));
  }

  async getStoreWithRatings(id: string): Promise<StoreWithRatings | undefined> {
    const [store] = await db.select().from(stores).where(eq(stores.id, id));
    if (!store) return undefined;

    const ratingsData = await db
      .select({
        rating: ratings,
        user: users,
      })
      .from(ratings)
      .innerJoin(users, eq(ratings.userId, users.id))
      .where(eq(ratings.storeId, id))
      .orderBy(desc(ratings.createdAt));

    const storeRatings = ratingsData.map(row => ({
      ...row.rating,
      user: row.user,
    }));

    const averageRating = storeRatings.length > 0 
      ? storeRatings.reduce((sum, r) => sum + r.ratingValue, 0) / storeRatings.length
      : 0;

    return {
      ...store,
      ratings: storeRatings,
      averageRating,
    };
  }

  // Rating methods
  async getRating(userId: string, storeId: string): Promise<Rating | undefined> {
    const [rating] = await db
      .select()
      .from(ratings)
      .where(and(eq(ratings.userId, userId), eq(ratings.storeId, storeId)));
    return rating || undefined;
  }

  async createRating(insertRating: InsertRating): Promise<Rating> {
    const [rating] = await db
      .insert(ratings)
      .values(insertRating)
      .returning();
    return rating;
  }

  async updateRating(userId: string, storeId: string, ratingValue: number): Promise<Rating | undefined> {
    const [rating] = await db
      .update(ratings)
      .set({ ratingValue })
      .where(and(eq(ratings.userId, userId), eq(ratings.storeId, storeId)))
      .returning();
    return rating || undefined;
  }

  async deleteRating(userId: string, storeId: string): Promise<boolean> {
    const result = await db
      .delete(ratings)
      .where(and(eq(ratings.userId, userId), eq(ratings.storeId, storeId)));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  async getRatingsByStore(storeId: string): Promise<RatingWithUser[]> {
    const result = await db
      .select({
        rating: ratings,
        user: users,
      })
      .from(ratings)
      .innerJoin(users, eq(ratings.userId, users.id))
      .where(eq(ratings.storeId, storeId))
      .orderBy(desc(ratings.createdAt));

    return result.map(row => ({
      ...row.rating,
      user: row.user,
    }));
  }

  async getUserRatings(userId: string): Promise<Rating[]> {
    return await db
      .select()
      .from(ratings)
      .where(eq(ratings.userId, userId))
      .orderBy(desc(ratings.createdAt));
  }

  // Stats methods
  async getStats(): Promise<{ totalUsers: number; totalStores: number; totalRatings: number; }> {
    const [userStats] = await db.select({ count: count() }).from(users);
    const [storeStats] = await db.select({ count: count() }).from(stores);
    const [ratingStats] = await db.select({ count: count() }).from(ratings);

    return {
      totalUsers: userStats.count || 0,
      totalStores: storeStats.count || 0,
      totalRatings: ratingStats.count || 0,
    };
  }
}

export const storage = new DatabaseStorage();

